def InitializeRow(entity, row):
ehAprovador = entity.EhAprovador() status = entity.Status.Index
if (row.GetCustomCommand("CMD_APROVAR")): row.GetCustomCommand("CMD_APROVAR").Enabled = ehAprovador and status == 1 if (row.GetCustomCommand("CMD_RECUSAR")): row.GetCustomCommand("CMD_RECUSAR").Enabled = ehAprovador and status == 1 if (row.GetCustomCommand("CMD_CANCELAR")): row.GetCustomCommand("CMD_CANCELAR").Enabled = ehAprovador and (status == 1 or status == 3) if (row.GetCustomCommand("CMD_LIBERAR")): row.GetCustomCommand("CMD_LIBERAR").Enabled = status == 3
