ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):

    import clr
    clr.AddReference("Benner.ERP.Comum")
    from Benner.ERP.Comum import Utils

    if row.GetCustomCommand("CMD_ELABORAR") != None:
        row.GetCustomCommand("CMD_ELABORAR").Enabled = entity.Fields["ESTADO"].Value == "E";
    if row.GetCustomCommand("CMD_REMANEJAR") != None:
        row.GetCustomCommand("CMD_REMANEJAR").Enabled = entity.Fields["ESTADO"].Value == "X";

    if row.GetCustomCommand("CMD_CARREGAR_OP"): row.GetCustomCommand("CMD_CARREGAR_OP").Enabled = Utils.EhOrcamentoPublicoComControleAcao()

    if (row.GetCustomCommand("CMD_DEFINIROFICIAL")): row.GetCustomCommand("CMD_DEFINIROFICIAL").Enabled = entity.ElaboracaoOficial == False
    if (row.GetCustomCommand("CMD_REMOVEROFICIAL")): row.GetCustomCommand("CMD_REMOVEROFICIAL").Enabled = entity.ElaboracaoOficial == True
