def InitializeRow(entity, row):
row.GetCellByName("EMISSAO").Text = entity.Fields["EMISSAO"].ToString("dd/MM/yyyy") row.GetCellByName("VENCIMENTO").Text = entity.Fields["VENCIMENTO"].ToString("dd/MM/yyyy") row.GetCellByName("VCTOPRORROGADO").Text = entity.Fields["VCTOPRORROGADO"].ToString("dd/MM/yyyy")
row.GetCellByName("VALOR").Text = entity.Fields["VALOR"].ToString("C2") row.GetCellByName("VALORBAIXADO").Text = entity.Fields["VALORBAIXADO"].ToString("C2") row.GetCellByName("SALDO").Text = entity.Fields["SALDO"].ToString("C2")
