def PreencherCamposPadroes(entity):
    from Benner.Tecnologia.Common import ListItem
    from System import DateTime
    entity.Fields["FILIAL"] = entity.Fields["DOCUMENTO"].Instance["FILIAL"].Value;
    entity.Fields["TIPOMOVIMENTO"] = ListItem(1, "1");
    entity.Fields["DATA"] = DateTime.Today;
