def Load(widget):
    from System import Decimal
    ent = widget.Entity
    if ( ent == None ):
        return;

    if ( ent["VALORBRUTO"].IsNull ):
        ent.Fields["VALORBRUTO"] = Decimal( 0.00 )
    if ( ent["VALORADIANTAMENTOADESCONTAR"].IsNull ):
        ent.Fields["VALORADIANTAMENTOADESCONTAR"] = Decimal( 0.00)
    if ( ent["VALORNOTASCREDITO"].IsNull ):
        ent.Fields["VALORNOTASCREDITO"] = Decimal( 0.00)        
    if ( ent["VALORNOTASDEBITO"].IsNull ):
        ent.Fields["VALORNOTASDEBITO"] = Decimal( 0.00)                
    if ( ent["SALDOFINAL"].IsNull ):
        ent.Fields["SALDOFINAL"] = Decimal( 0.00)
