def Load(widget):
  registro = widget.Entity

  if registro == None:
    #widget.View.Commands["New"].Visible = False
    #widget.View.Commands["Delete"].Visible = False
    #widget.View.Commands["Edit"].Visible = False
    registro = widget.Entity
  else:
    ehVisivel = registro.PermiteQtdDocumentosConsolidados()
    if (not ehVisivel):
      widget.View.Commands["C601DOCUMENTOSCANCELADOS"].Visible = False
