def InitializeRow(entity, row):
    from Benner.Tecnologia.Common import FieldDefinition
    from Benner.Tecnologia.Common import Entity
    from Benner.Tecnologia.Common import EntityDefinition

    if entity == None: return
    somenteLeitura = entity.SomenteLeitura()
    if(somenteLeitura):
		if row.EditCommand != None:
			row.EditCommand.Visible = False
		if row.DeleteCommand != None:
			row.DeleteCommand.Visible = False
