ScriptItemPortable.SourceCode:
import clr
clr.AddReference("Benner.Corporativo.Definicoes")
from Benner.Corporativo.Definicoes.Financeiro import FNDocumentoStatusListaItens

def Load(widget):
    if widget.Entity == None: return
    widget.View.Commands["CMD_EDITAR"].Visible = widget.Entity.Status != FNDocumentoStatusListaItens.ItemEmitida and widget.Entity.Status != FNDocumentoStatusListaItens.ItemAguardandoServidor;
