ScriptItemPortable.SourceCode:
def Load(widget):
    from Benner.Tecnologia.Common import FieldDefinition
    from Benner.Tecnologia.Common import Entity
    from Benner.Tecnologia.Common import EntityDefinition

    registro = widget.Entity

   # botaoSPEDVisible = registro.BotaoSPEDVisible()
   # if (not botaoSPEDVisible):
   #     widget.View.Commands["GRP_SPED"].Visible = False		

    botaoSpedGasEnergiaVisible = registro.BotaoSpedGasEnergiaVisible()
    if (not botaoSpedGasEnergiaVisible):
        if widget.View.Commands["GRP_SPED"].Items["CMD_GASENERGIAAGUA"] != None:
            widget.View.Commands["GRP_SPED"].Items["CMD_GASENERGIAAGUA"].Visible = False

    botaoSpedComunicacaoVisible = registro.BotaoSpedComunicacaoVisible()
    if (not botaoSpedComunicacaoVisible):
        if widget.View.Commands["GRP_SPED"].Items["CMD_COMUNICACAO"] != None:
            widget.View.Commands["GRP_SPED"].Items["CMD_COMUNICACAO"].Visible = False	

    botaoSpedTerminaisVisible = registro.BotaoSpedTerminalVisible()
    if (not botaoSpedTerminaisVisible):
        if widget.View.Commands["GRP_SPED"].Items["CMD_TERMINAISFAT"] != None:
            widget.View.Commands["GRP_SPED"].Items["CMD_TERMINAISFAT"].Visible = False

    botaoSpedComunicacaoVisible = registro.BotaoDocumentoAnuladoSubstituidoVisible()
    if (not botaoSpedComunicacaoVisible):
        if widget.View.Commands["GRP_COMANDOS"].Items["CMD_DOCANULADOSUBSTITUIDO"] != None:
            widget.View.Commands["GRP_COMANDOS"].Items["CMD_DOCANULADOSUBSTITUIDO"].Visible = False
