def OnCMD_EXCLUIRExecute(command, entity):
    command.RequestConfirmationBeforeExecute("Deseja excluir o documento e suas dependências?")
    if (entity["BLOQUETOEMITIDO"].GetInt32() > 0):
      command.RequestConfirmationBeforeExecute("Esse documento possui parcela que já teve bloqueto emitido, deseja continuar?")
