def InitializeRow(entity, row):
    RemovePrefixoMoedas("VALORTOTALITENS", entity, row)
    RemovePrefixoMoedas("BASECALCULO", entity, row)
    RemovePrefixoMoedas("VALORIMPOSTO", entity, row)
    RemovePrefixoMoedas("VALORDEDUCAOPISCOFINS", entity, row)

def RemovePrefixoMoedas(nomeCampo, entidade, linha):
    if entidade.Fields[nomeCampo] != None:
        linha.GetCellByName(nomeCampo).Text = str(str(str("{:,.2f}".format(entidade.Fields[nomeCampo])).replace(".","_")).replace(",",".")).replace("_",",").replace("-","");
