def InitializeRow(entity, row):
    row.GetCellByName("CUSTOM_CENTRODECUSTO").Text = entity.Fields["PATH_636301146316318914"] + " - " + entity.Fields["PATH_636301147004262571"]

    if (str(entity.Fields["PROJETO"]) != ""):
        row.GetCellByName("CUSTOM_PROJETO").Text = entity.Fields["PATH_636312233850762045"] + " - " + str(entity.Fields["PROJETO"])

    row.GetCellByName("CUSTOM_PERCENTUAL").Text = str("%0.2f" % entity.Fields["PERCENTUAL"]).replace(".",",")
