def InitializeRow(entity, row):
    row.GetCellByName("CONTAFINANCEIRA").Text = entity.Fields["PATH_636301123387212570"] + "-" + entity.Fields["PATH_636301114737614747"]
    if entity.Fields["NATUREZA"].Value == "C":
        row.GetCellByName("NATUREZA").Text = "C"
        row.GetCellByName("NATUREZA").Tooltip ="Crédito"
    else:
        row.GetCellByName("NATUREZA").Text = "D"
        row.GetCellByName("NATUREZA").Tooltip ="Débito"
