ScriptItemPortable.SourceCode:
def Load(widget):
    registro = widget.Entity

    if registro == None: return

    if widget.View.Commands["New"] != None: widget.View.Commands["New"].Visible = registro.EhRegistroFilho()
    if widget.View.Commands["Edit"] != None: widget.View.Commands["Edit"].Visible = registro.EhRegistroFilho()
    if widget.View.Commands["Delete"] != None: widget.View.Commands["Delete"].Visible = registro.EhRegistroFilho()
