def InitializeRow(entity, row):
    import clr 
    clr.AddReference("Benner.ERP.Comum")
    from Benner.ERP.Comum import Utils
    if(row.DeleteCommand <> None):
        row.DeleteCommand.Visible = Utils.VerificaPermissao("EXCLUIR_OCORRENCIAPARCELAS");

def Load(widget):
    import clr
    clr.AddReference("Benner.Erp.Web")
    from Benner.Erp.Web import AuthorizationHelper
    if widget.View.Commands["Edit"] != None:
        widget.View.Commands["Edit"].Visible = AuthorizationHelper.HasTask("MANTER_OCORRENCIAPARCELAS")

    if widget.View.Commands["New"] != None:
        widget.View.Commands["New"].Visible = AuthorizationHelper.HasTask("MANTER_OCORRENCIAPARCELAS")
