def OnCMD_LIBERARPARELAExecute(command, entity):
    if entity == None or entity.Handle.IsInvalid(): return

    estaEmInspecaoQualidade = entity.Fields["ESTAEMINSPECAOQUALIDADE"]
    if estaEmInspecaoQualidade == True:
        command.RequestConfirmationBeforeExecute("Existem itens em inspeção de qualidade, deseja liberar?")
    else:
        command.RequestConfirmationBeforeExecute("Confirma liberação da parcela?")

def OnCMD_SUSPENDERPARCELAExecute(command, entity):
    from Benner.Tecnologia.Common import EntityAssociation

    remessa = entity.Fields["REMESSA"]
    if remessa != None and remessa.Handle.IsValid():
        command.RequestConfirmationBeforeExecute("Parcela está em remessa bancária e será retirada automaticamente da remessa. Confirma a suspensão da parcela?")

def OnCMD_LIBERARPARCELAExecute(command, entity):

    if entity == None: return

    if entity.Fields["ESTAEMINSPECAOQUALIDADE"] == True:
        command.RequestConfirmationBeforeExecute("Existem itens em inspeção de qualidade, deseja liberar?")
    else:
        command.RequestConfirmationBeforeExecute("Confirma liberação da parcela?")

