def OnCMD_LIBERARExecute(command, entity):
    if entity == None: return

    if entity.Fields["ESTAEMINSPECAOQUALIDADE"] == True:
        command.RequestConfirmationBeforeExecute("Existem itens em inspeção de qualidade, deseja liberar?")
    else:
        command.RequestConfirmationBeforeExecute("Confirma liberação da parcela?")

def Load(widget):
    from Benner.Tecnologia.Common import EntityState

    registro = widget.Entity
    if registro == None:
        return

    if (widget.Entity.State != EntityState.Initialized):
        widget.View.Commands["BAIXA"].Visible = widget.Entity.PermiteBaixaDireta()
