def OnMOEDAValueChanged(view, entity):
    import clr
    clr.AddReference("Benner.Corporativo.Definicoes")

    from Benner.Corporativo.Definicoes.FluxoCaixa.Utils import FuncoesUtilsFluxo

    if (entity.Fields["MOEDA"].Handle.IsValid()):
      if (FuncoesUtilsFluxo.MoedaSelecionadaDiferenteDeReal(entity.Fields["MOEDA"].Handle)):
          view.FieldDefinitions["VALORCOTACAOMOEDA"].Visible = True
      else:
          view.FieldDefinitions["VALORCOTACAOMOEDA"].Visible = False
    else:
      view.FieldDefinitions["VALORCOTACAOMOEDA"].Visible = False

def OnSELECIONARGRUPOSValueChanged(view, entity):
    import clr
    clr.AddReference("Benner.Corporativo.Definicoes")

    from Benner.Corporativo.Definicoes.FluxoCaixa.Utils import FuncoesUtilsFluxo

    if (entity.Fields["SELECIONARGRUPOS"].Value == "M"):
        entity.Fields["GRUPOSLANCAMENTO"].Clear()
        entity.Fields["GRUPOSLANCAMENTO"].Add(FuncoesUtilsFluxo.GetAllHandlesGrupos())

    if (entity.Fields["SELECIONARGRUPOS"].Value == "D"):
        entity.Fields["GRUPOSLANCAMENTO"].Clear()
