def OnFORMACALCULOMULTAValueChanged(view, entity):
	from Benner.Tecnologia.Common import FieldDefinition
	if (int(entity.Fields["FORMACALCULOMULTA"].Value) == 1):
		view.FieldDefinitions["VALORMULTA"].DataAccessLevel = FieldDefinition.AccessLevel.Read
		view.FieldDefinitions["PERCENTUALMULTA"].DataAccessLevel = FieldDefinition.AccessLevel.Edit
	else :
		view.FieldDefinitions["VALORMULTA"].DataAccessLevel = FieldDefinition.AccessLevel.Edit
		view.FieldDefinitions["PERCENTUALMULTA"].DataAccessLevel = FieldDefinition.AccessLevel.Read

def OnFORMACALCULOJUROSDIAValueChanged(view, entity):
	from Benner.Tecnologia.Common import FieldDefinition
	if (int(entity.Fields["FORMACALCULOJUROSDIA"].Value) == 1):
		view.FieldDefinitions["VALORJUROSDIA"].DataAccessLevel = FieldDefinition.AccessLevel.Read
		view.FieldDefinitions["PERCENTUALJUROSDIA"].DataAccessLevel = FieldDefinition.AccessLevel.Edit
	else :
		view.FieldDefinitions["VALORJUROSDIA"].DataAccessLevel = FieldDefinition.AccessLevel.Edit
		view.FieldDefinitions["PERCENTUALJUROSDIA"].DataAccessLevel = FieldDefinition.AccessLevel.Read

def Load(widget):
	from Benner.Tecnologia.Common import FieldDefinition
	entity = widget.Entity
	view = widget.View
	if (int(entity.Fields["FORMACALCULOMULTA"].Value) == 1):
		view.FieldDefinitions["VALORMULTA"].DataAccessLevel = FieldDefinition.AccessLevel.Read
		view.FieldDefinitions["PERCENTUALMULTA"].DataAccessLevel = FieldDefinition.AccessLevel.Edit
	else :
		view.FieldDefinitions["VALORMULTA"].DataAccessLevel = FieldDefinition.AccessLevel.Edit
		view.FieldDefinitions["PERCENTUALMULTA"].DataAccessLevel = FieldDefinition.AccessLevel.Read

	if (int(entity.Fields["FORMACALCULOJUROSDIA"].Value) == 1):
		view.FieldDefinitions["VALORJUROSDIA"].DataAccessLevel = FieldDefinition.AccessLevel.Read
		view.FieldDefinitions["PERCENTUALJUROSDIA"].DataAccessLevel = FieldDefinition.AccessLevel.Edit
	else :
		view.FieldDefinitions["VALORJUROSDIA"].DataAccessLevel = FieldDefinition.AccessLevel.Edit
		view.FieldDefinitions["PERCENTUALJUROSDIA"].DataAccessLevel = FieldDefinition.AccessLevel.Read

def OnORIGEMValueChanged(view, entity):
	from Benner.Tecnologia.Common import RadioItem
	from Benner.Tecnologia.Common import FieldDefinition

	if (entity.Fields["ORIGEM"].Value == "P") :
		entity.Fields["NATUREZA"] = "D"
		view.FieldDefinitions["NATUREZA"].DataAccessLevel = FieldDefinition.AccessLevel.Read

	if (entity.Fields["ORIGEM"].Value == "R") :
		entity.Fields["NATUREZA"] = "C"
		view.FieldDefinitions["NATUREZA"].DataAccessLevel = FieldDefinition.AccessLevel.Read

	if (entity.Fields["ORIGEM"].Value == "T") :
		view.FieldDefinitions["NATUREZA"].DataAccessLevel = FieldDefinition.AccessLevel.Edit
