ScriptItemPortable.SourceCode:
def Load(widget):
	from Benner.Tecnologia.Common import FieldDefinition
	registro = widget.Entity
	if registro == None: return

	if (registro.Fields["ACAOALTERARVENCTO"] is not None):
		if (registro.Fields["ACAOALTERARVENCTO"].Value == 3):
			widget.View.FieldDefinitions["NOVADATAVENC"].Visible = True
			widget.View.FieldDefinitions["QTDIASALTERARVENCTO"].Visible = False
		else:
			widget.View.FieldDefinitions["NOVADATAVENC"].Visible = False
			widget.View.FieldDefinitions["QTDIASALTERARVENCTO"].Visible = True
	else:
		widget.View.FieldDefinitions["NOVADATAVENC"].Visible = False

def OnACAOALTERARVENCTOValueChanged(view, entity):
	from Benner.Tecnologia.Common import FieldDefinition
	registro = entity
	if registro == None: return

	if (registro.Fields["ACAOALTERARVENCTO"] is not None):
		if (registro.Fields["ACAOALTERARVENCTO"].Value == 3):
			view.FieldDefinitions["NOVADATAVENC"].Visible = True
			view.FieldDefinitions["QTDIASALTERARVENCTO"].Visible = False
		else:
			view.FieldDefinitions["NOVADATAVENC"].Visible = False
			view.FieldDefinitions["QTDIASALTERARVENCTO"].Visible = True
	else:
		view.FieldDefinitions["NOVADATAVENC"].Visible = False
