def OnFLUXOValueChanged(view, entity):
fluxo = entity.Fields["FLUXO"].Instance if fluxo == None: return
entity.Fields["DATAINICIO"] = fluxo.Fields["DATAINICIO"] entity.Fields["DATAFIM"] = fluxo.Fields["DATAFIM"]

def OnTIPOFLUXOValueChanged(view, entity):

    if entity == None: 
        return

    entity.Fields["FLUXO"].Instance = None
