def OnViewExecute(command, entity):


    if entity["TIPO"].GetInt64() == 2 :
        command.OutputDefinition.Url = "~/aga/a/materiais/recebimentoFisico/RecebimentoFisicoForm.aspx"
        command.OutputDefinition.TargetEntityDefinitionName = "CP_RECEBIMENTOFISICOPAI"        
        command.OutputDefinition.TargetEntityHandle = entity["HANDLE"].GetInt64()            

    if entity["TIPO"].GetInt64() == 3 :    
        command.OutputDefinition.Url = "~/aga/a/materiais/recebimentos/RecebimentoFiscal/RecebimentoFiscalForm.aspx"
        command.OutputDefinition.TargetEntityDefinitionName = "FN_DOCUMENTOS"
        command.OutputDefinition.TargetEntityHandle = entity["HANDLE"].GetInt64()
