ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    fields = ["ARQUIVOGERADO", "CONFIRMACAO1", "CONFIRMACAO2"]

    for field in fields:
        row.GetCellByName("CUSTOM_" + field).Align = "center"
        row.GetCellByName("CUSTOM_" + field).TextColor = "#3CB371" if entity.Fields[field] else "#B22222"
        row.GetCellByName("CUSTOM_" + field).FontIcon = "fa fa-check" if entity.Fields[field] else "fa fa-times"

    row.GetCellByName("TOTALTITULOS").Align = "center"    

    if row.DeleteCommand != None:
		row.DeleteCommand.Visible = (entity["TOTALTITULOS"].GetInt32() == 0)
