def Load(widget):
    import clr
    clr.AddReference("Benner.ERP.Comum")
    from Benner.ERP.Comum import Utils
    widget.View.Commands["New"].Visible = Utils.VerificaPermissao("MANTERFORNECEDOR");
    widget.View.Commands["Edit"].Visible = Utils.VerificaPermissao("MANTERFORNECEDOR");
    widget.View.Commands["Delete"].Visible = Utils.VerificaPermissao("EXCLUIRFORNECEDOR");
