def InitializeRow(entity, row):
    import clr
    clr.AddReference("Benner.ERP.Comum")
    from Benner.ERP.Comum import Utils
    if row.EditCommand != None: row.EditCommand.Visible = Utils.VerificaPermissao("MANTERFORNECEDOR");
    if row.DeleteCommand != None: row.DeleteCommand.Visible = Utils.VerificaPermissao("EXCLUIRFORNECEDOR");
