ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    if ( entity == None ): return

    row.GetCellByName("CUSTOM_TIPO").Text = entity.Fields["LIMITECREDITO"]
    if ( entity["LIMITECREDITO"].GetInt32() == 2 ):
        row.GetCellByName("CUSTOM_TIPO").Text =  row.GetCellByName("CUSTOM_TIPO").Text + " - " + entity.Fields["PESSOALIMITE"].Instance.Fields["NOME"];
        row.GetCellByName("VALORLIMITE").Text = ""
