ScriptItemPortable.SourceCode:
def Load(widget):
    import clr
    clr.AddReference("Benner.Corporativo.Definicoes")

    form = widget.ProviderWidget.Entity
    if form.Fields["PROJETO"].Handle.IsInvalid() : return;
    handle = form.Fields["PROJETO"].Handle.Value;
    if handle > 0 : widget.View.Commands["New"].Visible = False

def InitializeRow(entity, row):
    import clr
    clr.AddReference("Benner.Corporativo.Definicoes")

    form = row.Grid.ProviderWidget.Entity
    if form.Fields["PROJETO"].Handle.IsInvalid() : return;
    handle = form.Fields["PROJETO"].Handle.Value;
    if handle > 0 : 
        row.DeleteCommand.Visible = False
        row.EditCommand.Visible = False
