ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    import clr
    clr.AddReference("Benner.Corporativo.Definicoes")
    from Benner.Corporativo.Definicoes.GestaoDeProjetos import GPProjetoLinhasBase

    if (row.GetCustomCommand("CMD_HISTORICO") != None):
        row.GetCustomCommand("CMD_HISTORICO").Enabled = (entity.Fields["GRUPOASSINATURAS"].Instance != None)

    if (row.GetCustomCommand("CMD_CONFIRMAR") != None and row.GetCustomCommand("CMD_RECUSAR") != None):
        emAprovacao = GPProjetoLinhasBase.PermissaoConfirmarRecusar(entity.Fields["HANDLE"].Value)
        row.GetCustomCommand("CMD_CONFIRMAR").Enabled = emAprovacao
        row.GetCustomCommand("CMD_RECUSAR").Enabled = emAprovacao
