ScriptItemPortable.SourceCode:
def Load(widget):
    from Benner.Corporativo.Definicoes.GestaoDeProjetos import GPProjetos
    from Benner.Tecnologia.Common import BennerContext
    projetoEntity = GPProjetos.Get(widget.ProviderWidget.Entity.ProjetoHandle)
    usuarioEhGestor = BennerContext.Security.GetLoggedUserHandle() ==projetoEntity.GestorProjetoHandle
    entity = widget.ProviderWidget.Entity
    if (entity != None and entity.Fields["TAREFAAGRUPADORA"]): widget.Hidden = True

    status = 0;
    if widget.ProviderWidget.Entity.Fields["STATUS"] != None:
        status = widget.ProviderWidget.Entity.Fields["STATUS"].Value;

    if widget.View.Commands["New"] != None:
        widget.View.Commands["New"].Visible = (status != 3 and not widget.ProviderWidget.Entity.Fields["PROJETOLINHABASE"].Handle.IsValid()) and usuarioEhGestor
