ScriptItemPortable.SourceCode:
def Load(widget):
    from Benner.Tecnologia.Common import BennerContext
    usuarioEhGestor = BennerContext.Security.GetLoggedUserHandle() == widget.ProviderWidget.Entity.Fields["GESTORPROJETO"].Handle

    status = 0;
    if widget.ProviderWidget.Entity.Fields["STATUS"] != None:
        status = widget.ProviderWidget.Entity.Fields["STATUS"].Value;

    if widget.View.Commands["New"] != None : widget.View.Commands["New"].Visible = (status in (1, 2)) and usuarioEhGestor

def InitializeRow(entity, row):
    import clr
    clr.AddReference("Benner.Corporativo.Definicoes")
    from Benner.Corporativo.Definicoes.GestaoDeProjetos import GPProjetos
    from Benner.Tecnologia.Common import BennerContext
    projetoEntity = GPProjetos.Get(entity.ProjetoHandle)
    usuarioEhGestor = BennerContext.Security.GetLoggedUserHandle() == projetoEntity.GestorProjetoHandle

    projetoConcluido = GPProjetos.ProjetoConcluido(entity.ProjetoHandle)

    status = 0;
    if entity.Fields["STATUS"] != None:
        status = entity.Fields["STATUS"].Value;

    if row.EditCommand != None: row.EditCommand.Visible = (not projetoConcluido and status != 3) and usuarioEhGestor
    if row.DeleteCommand != None: row.DeleteCommand.Visible = (not projetoConcluido and status == 1) and usuarioEhGestor
