ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    row.GetCellByName("REPOSICAO").Align = "center"
    row.GetCellByName("REPOSICAO").TextColor = "#3CB371" if entity.Fields["REPOSICAO"] == "S" else "#B22222"
    row.GetCellByName("REPOSICAO").FontIcon = "fa fa-check" if entity.Fields["REPOSICAO"] == "S"  else "fa fa-times"
    row.GetCellByName("DATAEMISSAO").Text = entity.Fields["DATAEMISSAO"].ToString("dd/MM/yyyy");
