ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    from Benner.Tecnologia.Common import ListItem
    from System import DateTime
    from System import Convert
    from System import Math

    row.GetCustomCommand("CmdAtender").Enabled = False;
    row.GetCustomCommand("CmdResolver").Enabled = False;
    row.GetCustomCommand("CmdConcluir").Enabled = False;

    if entity.Fields["STATUS"] == ListItem(1, "Aberto"):
        row.GetCustomCommand("CmdAtender").Enabled = True;
    elif entity.Fields["STATUS"] == ListItem(2, "Em Andamento"):
        row.GetCustomCommand("CmdResolver").Enabled = True;
    elif entity.Fields["STATUS"] == ListItem(3, "Atendido"):
        if ((int)(Math.Abs((DateTime.Now - Convert.ToDateTime(entity.Fields["DATAALTERACAO"])).TotalDays))) > 7:
            row.GetCustomCommand("CmdConcluir").Enabled = True;
