ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):
    from Benner.Tecnologia.Common import Criteria
    from Benner.Tecnologia.Common import Parameter
    from Benner.Tecnologia.Common import QuerySource
    from Benner.Tecnologia.Common import EntityDefinition
    from Benner.Tecnologia.Common import Entity

    sql = "SELECT CASE WHEN (SELECT FORMAT((B.VALORMETA - SUM(A.VALORMETA)),'C', 'pt-BR') SALDO FROM K_CRM_METASUSUARIOSVERTICAL A JOIN K_CRM_METASUSUARIOS B ON B.HANDLE = A.METAUSUARIO WHERE B.HANDLE =  :METAUSUARIO GROUP BY B.VALORMETA) IS NULL THEN (SELECT FORMAT((VALORMETA),'C', 'pt-BR') SALDO FROM K_CRM_METASUSUARIOS WHERE HANDLE =  :METAVERTICAL) ELSE (SELECT FORMAT((B.VALORMETA - SUM(A.VALORMETA)),'C', 'pt-BR') SALDO FROM K_CRM_METASUSUARIOSVERTICAL A JOIN K_CRM_METASUSUARIOS B ON B.HANDLE = A.METAUSUARIO WHERE B.HANDLE = :METAUSUARIO GROUP BY B.VALORMETA) END SALDO";
    crit = Criteria()
    crit.Parameters.Add(Parameter("METAUSUARIO", entity.Handle))
    ed = EntityDefinition.Create()
    ed.EntitySource = QuerySource(sql)
    saldo = Entity.GetFirstOrDefault(ed, crit)

    if saldo == None:
        row.GetCellByName("SALDO").Text = "R$ 0,00";
    elif saldo.Fields["SALDO"] != None:
        row.GetCellByName("SALDO").Text = saldo.Fields["SALDO"].ToString()
