def OncmdAtualizaPropostaExecute(command, entity): from System import DateTime from Benner.Tecnologia.Common import EntityBase from Benner.Tecnologia.Common import Entities from Benner.Tecnologia.Common import EntityDefinition from Benner.Tecnologia.Common import EntityState from Benner.Tecnologia.Common import Entity from Benner.Tecnologia.Common import Criteria from Benner.Tecnologia.Common import Parameter from Benner.Tecnologia.Common import GetMode from Benner.Tecnologia.Common import BennerIdentity
oportunidade = entity.Fields["HANDLE"]; total = 0 horas = 0
if oportunidade.IsValid(): criteria = Criteria("A.OPORTUNIDADE = :OPORTUNIDADE", Parameter("OPORTUNIDADE", oportunidade)) itens = Entity.GetMany(EntityDefinition.GetByName("K_CRM_PESSOAOPORTUNIDADESITENS"), criteria)
if itens.Count > 0:
for item in itens: total += (item.Fields["ITEM"].Instance.Fields["VALOR"]*(100 - item.Fields["VALORDESCONTO"])/100) horas += item.Fields["ITEM"].Instance.Fields["HORAS"]
entity.Fields["VALORTOTAL"] = total entity.Fields["QDADEHRS"] = horas

def Load(widget):
    import System
    from System import DateTime
    from System import Convert
    from Benner.Tecnologia.Common import EntityBase
    from Benner.Tecnologia.Common import Entities
