    if entity.Fields["HANDLE"].IsValid():
        oportunidade = entity.Fields["CODIGO"];
        versao = entity.Fields["VERSAO"].ToString();

        cri = Criteria("A.OPORTUNIDADE = :OPORTUNIDADE AND A.VERSAO = :VERSAO AND A.STATUS = 3");
        cri.Parameters.Add(Parameter("OPORTUNIDADE", entity.Handle))
        cri.Parameters.Add(Parameter("VERSAO", versao))
        itens = Entity.GetMany(EntityDefinition.GetByName("K_CRM_PESSOAOPORTUNIDADESITENS"), cri)

        widget.View.Commands["CmdAtivar"].Visible = itens.Count > 0

        if entity.Fields["MODALIDADE"].Handle.IsInvalid():
            view.FieldDefinitions["CONDPAGLU"].Visible = False;
            view.FieldDefinitions["CONDPAGLUM"].Visible = False;
            view.FieldDefinitions["CONDPAGLOC"].Visible = False;
            view.FieldDefinitions["CONDPAGSERV"].Visible = False;
            view.FieldDefinitions["CONDPAGSAAS"].Visible = False;
            view.FieldDefinitions["CONDPAGBPO"].Visible = False;
        else:
            criteria = Criteria("A.HANDLE IN (SELECT MODALIDADE FROM K_CRM_PESSOAOPORTUNIDADES WHERE CODIGO = :CODIGO AND VERSAO = :VERSAO)");
            criteria.Parameters.Add(Parameter("CODIGO", oportunidade))
            criteria.Parameters.Add(Parameter("VERSAO", versao))
            modalidade = Entity.Get(EntityDefinition.GetByName("K_CRM_MODALIDADES"), criteria);



