def Load(widget):
    from Benner.Tecnologia.Common import FieldDefinition
    entity = widget.Entity;
    view   = widget.View;

    if entity == None or view == None: return

    if entity.Handle.IsValid():
        #view.FieldDefinitions["ATIVIDADE"].DataAccessLevel  = FieldDefinition.AccessLevel.Read
        view.FieldDefinitions["ITEM"].DataAccessLevel  = FieldDefinition.AccessLevel.Read
