def OnADICIONALValueChanged(view, entity):
    if entity == None:
        return;

    if entity.Fields["ADICIONAL"].Instance.Fields["NOME"] == "Forma de Pagamento":
        entity.Fields["DESCRICAO"] = "A forma de pagamento dessa proposta ser√° a vista mediante pagamento de boleto.";
    else:
        entity.Fields["DESCRICAO"] = "";
