ScriptItemPortable.SourceCode:
def Load(widget):

    if (widget.Entity == None):
        return;

    widget.View.FieldDefinitions["PRODUTO"].Visible = widget.Entity.Fields["PRODUTO"].Handle.IsValid();
    widget.View.FieldDefinitions["TIPORECURSO"].Visible = widget.Entity.Fields["TIPORECURSO"].Handle.IsValid();

    widget.View.FieldDefinitions["PRODUTOCONTRATO"].Visible = widget.Entity.Fields["PRODUTOCONTRATO"] != None;
    widget.View.FieldDefinitions["NUMEROCONTRATO"].Visible = widget.Entity.Fields["NUMEROCONTRATO"] != None;
