ScriptItemPortable.SourceCode:
def Load(widget):
    import clr
    clr.AddReference("Benner.Tecnologia.Wes.Components")
    from Benner.Tecnologia.Wes.Components import MessageType
    from Benner.Tecnologia.Common import Entity

    entity = widget.Entity;
    nova = entity.Fields["MESMOPERIODO"];

    if nova == True:
        entity["NOVAVIGENCIA"].Visible = False;
    else:
        entity["NOVAVIGENCIA"].Visible = True;

    widget.MessagePanel.ShowMessage("A clonagem excluirá qualquer registro anterior dentro deste periodo de vigência nas filiais de destino.",MessageType.Information)

def OnMESMOPERIODOValueChanged(view, entity):
    from Benner.Tecnologia.Common import Entity

    nova = entity.Fields["MESMOPERIODO"];

    if nova == True:
        entity["NOVAVIGENCIA"].Visible = False;
    else:
        entity["NOVAVIGENCIA"].Visible = True;
