ScriptItemPortable.SourceCode:
def OnPESQUISAPORValueChanged(view, entity):

    if entity.Fields["PESQUISAPOR"] == None:
        view.FieldDefinitions["CNPJ"].Visible = False;
        view.FieldDefinitions["CNPJ"].Required = False;
        view.FieldDefinitions["RAZAOSOCIAL"].Visible = False;
        view.FieldDefinitions["RAZAOSOCIAL"].Required = False;
        view.FieldDefinitions["NOMEFANTASIA"].Visible = False;
        view.FieldDefinitions["NOMEFANTASIA"].Required = False;
    else:
        if entity.Fields["PESQUISAPOR"].Value == 1:
            view.FieldDefinitions["CNPJ"].Visible = True;
            view.FieldDefinitions["CNPJ"].Required = True;
            view.FieldDefinitions["RAZAOSOCIAL"].Visible = False;
            view.FieldDefinitions["RAZAOSOCIAL"].Required = False;
            view.FieldDefinitions["NOMEFANTASIA"].Visible = False;
            view.FieldDefinitions["NOMEFANTASIA"].Required = False;
        elif entity.Fields["PESQUISAPOR"].Value == 2:
            view.FieldDefinitions["CNPJ"].Visible = False;
            view.FieldDefinitions["CNPJ"].Required = False;
            view.FieldDefinitions["RAZAOSOCIAL"].Visible = True;
            view.FieldDefinitions["RAZAOSOCIAL"].Required = True;
            view.FieldDefinitions["NOMEFANTASIA"].Visible = False;
            view.FieldDefinitions["NOMEFANTASIA"].Required = False;
        elif entity.Fields["PESQUISAPOR"].Value == 3:
            view.FieldDefinitions["CNPJ"].Visible = False;
            view.FieldDefinitions["CNPJ"].Required = False;
            view.FieldDefinitions["RAZAOSOCIAL"].Visible = False;
            view.FieldDefinitions["RAZAOSOCIAL"].Required = False;
            view.FieldDefinitions["NOMEFANTASIA"].Visible = True;
            view.FieldDefinitions["NOMEFANTASIA"].Required = True;
        else:
            view.FieldDefinitions["CNPJ"].Visible = False;
            view.FieldDefinitions["CNPJ"].Required = False;
            view.FieldDefinitions["RAZAOSOCIAL"].Visible = False;
            view.FieldDefinitions["RAZAOSOCIAL"].Required = False;
            view.FieldDefinitions["NOMEFANTASIA"].Visible = False;
            view.FieldDefinitions["NOMEFANTASIA"].Required = False;
