ScriptItemPortable.SourceCode:
def Load(widget):
    if (widget.Entity == None):
        return

    if widget.Entity.Fields["ACOPLADO"]:
        widget.View.Commands["CMD_ACOPLARVEICULO"].Visible = False
    elif widget.Entity.Fields["DESACOPLADO"]:
        widget.View.Commands["CMD_ACOPLARVEICULO"].Visible = False
        widget.View.Commands["CMD_DESACOPLARVEICULO"].Visible = False
    else:
        widget.View.Commands["CMD_VOLTARVEICULO"].Visible = False
        widget.View.Commands["CMD_DESACOPLARVEICULO"].Visible = False
