ScriptItemPortable.SourceCode:
def Load(widget):

	entity = widget.Entity
	if entity == None: return

	if widget.View.Commands["CMD_Liberar"] != None:
		widget.View.Commands["CMD_Liberar"].Visible = (entity.Fields["STATUS"].Value == 1)

	if widget.View.Commands["CMD_EfetuarSaida"] != None:
		widget.View.Commands["CMD_EfetuarSaida"].Visible = (entity.Fields["STATUS"].Value == 3)

	if widget.View.Commands["CMD_EfetuarRetorno"] != None:
		widget.View.Commands["CMD_EfetuarRetorno"].Visible = (entity.Fields["STATUS"].Value == 4)

	if widget.View.Commands["CMD_VoltarStatus"] != None:
		widget.View.Commands["CMD_VoltarStatus"].Visible = (entity.Fields["STATUS"].Value >= 3)
