ScriptItemPortable.SourceCode:
def InitializeRow(entity, row):

    if (entity.Fields["STATUS"] != None):
		if (entity.Fields["STATUS"].Value == 1):
			row.GetCellByName("CUSTOM_STATUSS").Text = "<span class='label-roxo letra-roxo '> Cadastrado </span>"
		elif (entity.Fields["STATUS"].Value == 2):
			row.GetCellByName("CUSTOM_STATUSS").Text = "<span class='letra-amarelo label-amarelo'> Aguardando confirmação </span>"
		elif (entity.Fields["STATUS"].Value == 3):
			row.GetCellByName("CUSTOM_STATUSS").Text = "<span class='letra-amarelo label-amarelo'> Aguardando saída </span>"
		elif (entity.Fields["STATUS"].Value == 4):
			row.GetCellByName("CUSTOM_STATUSS").Text = "<span class='letra-amarelo label-amarelo'> Aguardando retorno </span>"
		elif (entity.Fields["STATUS"].Value == 5):
			row.GetCellByName("CUSTOM_STATUSS").Text = "<span class='letra-azul label-azul'> Encerrada </span>"
		elif (entity.Fields["STATUS"].Value == 6):
			row.GetCellByName("CUSTOM_STATUSS").Text = "<span class='letra-vermelho label-vermelho'> Cancelada </span>"
		elif (entity.Fields["STATUS"].Value == 7):
			row.GetCellByName("CUSTOM_STATUSS").Text = "<span class='letra-vermelho label-vermelho'> Recusada </span>"
