def Load(widget):
    def InitializeRow(entity, row):
        if (entity == None): return  # Nota: '== None' pode ser substituído por 'is None'

        situacao = entity.Fields["STATUS"].Value;

        if situacao == 1:
            row.GetCellByName("STATUS").Text = "<span class='label label-info'> Cadastrado </span>"
        elif situacao == 2:
            row.GetCellByName("STATUS").Text = "<span class='label label-warning'> Em aprovação </span>"
        elif situacao == 3:
            row.GetCellByName("STATUS").Text = "<span class='label label-success'> Aprovado </span>"
        elif situacao == 4:
            row.GetCellByName("STATUS").Text = "<span class='label label-danger'> Cancelado </span>"
        elif situacao == 5:
            row.GetCellByName("STATUS").Text = "<span class='label label-success'> Encerrado </span>"
        elif situacao == 6:
            row.GetCellByName("STATUS").Text = "<span class='label label-warning'> Recusado </span>"
        elif situacao == 7:
            row.GetCellByName("STATUS").Text = "<span class='label label-info'> Aguardando pagamento </span>"
        elif situacao == 8:
            row.GetCellByName("STATUS").Text = "<span class='label label-info'> Em preparação para pagamento </span>"
        elif situacao == 9:
            row.GetCellByName("STATUS").Text = "<span class='label label-warning'> Verificando dados inconsistentes </span>"
        elif situacao == 10:
            row.GetCellByName("STATUS").Text = "<span class='label label-info'> Em prestação de contas </span>"
        elif situacao == 11:
            row.GetCellByName("STATUS").Text = "<span class='label label-success'> Pagamento efetuado </span>"
        elif situacao == 12:
            row.GetCellByName("STATUS").Text = "<span class='label label-info'> Aguardando integração </span>"
        elif situacao == 13:
            row.GetCellByName("STATUS").Text = "<span class='label label-info'> Aguardando comprovação </span>"
        elif situacao == 14:
            row.GetCellByName("STATUS").Text = "<span class='label label-success'> Pagamento liquidado </span>"
        elif situacao == 15:
            row.GetCellByName("STATUS").Text = "<span class='label label-warning'> Recusa comprovação de pagamento </span>"
        elif situacao == 16:
            row.GetCellByName("STATUS").Text = "<span class='label label-warning'> Em conferência de adiantamentos </span>"
