ScriptItemPortable.SourceCode:
def OnSaveExecute(command, entity):
    if entity.Fields["CONJUNTO"].Count > 0:
        command.RequestConfirmationBeforeExecute("Confirma o ajuste do(s) conjunto(s) selecionado(s)?")
    else:
        command.RequestConfirmationBeforeExecute("Confirma o ajuste de todos os conjuntos?")

def Load(widget):
    if not (widget.Entity.Fields["VEICULO"].Handle.IsValid()):
        widget.View.Commands["Save"].Visible = False
        widget.View.Commands["Cancel"].Visible = False

def OnVEICULOValueChanged(view, entity):
      if entity.Fields["VEICULO"].Handle.IsValid():
          view.Commands["Save"].Visible = True
          view.Commands["Cancel"].Visible = True
