def InitializeRow(entity, row):
row.EditCommand.Visible = entity.PodeAlterar row.DeleteCommand.Visible = entity.PodeExcluir
if (row.GetCustomCommand("CMD_LIBERAR")): row.GetCustomCommand("CMD_LIBERAR").Enabled = entity.PodeLiberar if (row.GetCustomCommand("CMD_RECUSAR")): row.GetCustomCommand("CMD_RECUSAR").Enabled = entity.PodeConfirmarOuRecusar if (row.GetCustomCommand("CMD_CONFIRMAR")): row.GetCustomCommand("CMD_CONFIRMAR").Enabled = entity.PodeConfirmarOuRecusar if (row.GetCustomCommand("CMD_VOLTAR")): row.GetCustomCommand("CMD_VOLTAR").Enabled = entity.PodeVoltar
