ScriptItemPortable.SourceCode:
def OnIDENTIFICARESULTADOValueChanged(view, entity):

    entity.Fields["CODIGO"] = "";

    try:
        lookup = entity.Fields["CATEGORIAECONOMICA"];
        entity.Fields["CODIGO"] += lookup.Instance.Fields["CODIGO"] + ".";
    except:
        print("An exception occurred")

    try:
        lookup = entity.Fields["GRUPONATUREZA"];
        entity.Fields["CODIGO"] += lookup.Instance.Fields["CODIGO"] + ".";
    except:
        print("An exception occurred")        

    try:
        lookup = entity.Fields["MODALIDADEAPLICACAO"];
        entity.Fields["CODIGO"] += lookup.Instance.Fields["CODIGO"] + ".";
    except:
        print("An exception occurred")                

    try:
        lookup = entity.Fields["DESPESA"];
        entity.Fields["CODIGO"] += lookup.Instance.Fields["CODIGO"] + ".";
    except:
        print("An exception occurred")                        

    try:
        lookup = entity.Fields["SUBELEMENTO"];
        entity.Fields["CODIGO"] += lookup.Instance.Fields["CODIGO"] + ".";
    except:
        print("An exception occurred")

    string = entity.Fields["CODIGO"];
    entity.Fields["CODIGO"] = string[0:len(string)-1];
