ScriptItemPortable.SourceCode:
def OnFUNCAOValueChanged(view, entity):

    entity.Fields["CODIGO"] = "";

    try:
        lookup = entity.Fields["FUNCAO"];
        entity.Fields["CODIGO"] += lookup.Instance.Fields["CODIGO"] + ".";
    except:
        print("An exception occurred")

    try:
        lookup = entity.Fields["SUBFUNCAO"];
        entity.Fields["CODIGO"] += lookup.Instance.Fields["CODIGO"] + ".";
    except:
        print("An exception occurred")        

    try:
        lookup = entity.Fields["PROGRAMA"];
        entity.Fields["CODIGO"] += lookup.Instance.Fields["CODIGO"] + ".";
    except:
        print("An exception occurred")                

    try:
        lookup = entity.Fields["ACAO"];
        entity.Fields["CODIGO"] += lookup.Instance.Fields["CODIGO"] + ".";
    except:
        print("An exception occurred")                        

    try:
        lookup = entity.Fields["SUBTITULO"];
        entity.Fields["CODIGO"] += lookup.Instance.Fields["CODIGO"] + ".";
    except:
        print("An exception occurred")

    string = entity.Fields["CODIGO"];
    entity.Fields["CODIGO"] = string[0:len(string)-1];

