def InitializeRow(entity, row):
    if entity["ATIVO"].GetString() == "N" :
        row.GetCellByName("VIGENCIA").Text = "<div class='label label-sm' style='background-color: #bbbbbb !important; padding: 2px 7px !important;'><i class='fa fa-times-circle'></i><b  style='padding-left: 5px'>Fora de Vigência</b></div>";
        row.GetCellByName("VIGENCIA").Tooltip ="Fora de Vigência"
    else:
        row.GetCellByName("VIGENCIA").Text = "<div class='label label-sm' style='background-color: #1BBC9B !important; padding: 2px 7px !important;'><i class='fa fa-check-circle'></i><b style='padding-left: 5px'>Em Vigência</b></div>";
        row.GetCellByName("VIGENCIA").Tooltip ="Em Vigência"
