def InitializeRow(entity, row):
     if (row.GetCustomCommand("CMD_DEFINIROFICIAL")): row.GetCustomCommand("CMD_DEFINIROFICIAL").Enabled = entity.Oficial == False
     if (row.GetCustomCommand("CMD_REMOVEROFICIAL")): row.GetCustomCommand("CMD_REMOVEROFICIAL").Enabled = entity.Oficial == True
